INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','1','0','Everybody onboard! This caravan\'s leaving for the Mor\'shan Rampart!','14','0','100','0','0','0','say1','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','2','0','How are you doing back there? Not too bumpy I hope.','12','0','100','0','0','0','say2','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','3','0','Our enemies wouldn\'t dare attack us this close to the Crossroads. It\'s further out that we\'ll have to watch our backs.','12','0','100','0','0','0','say3','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','4','0','My family has been running caravan routes in the Barrens since Orgrimmar was settled. We raise and train the kodo\'s ourselves. My Sister, Nagala is the best kodo breeder outside of Mulgore.','12','0','100','0','0','0','say4','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','5','0','Bet you didn\'t know a kodo can eat it\'s own weight in grass each day. Sure is a lot to shovel when it comes out the other end.','12','0','100','0','0','0','say5','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','6','0','Hope you don\'t mind, but we\'ll stop at Dreadmist Camp to pick up additional supplies and fresh mounts for the last leg of the journey.','12','0','100','0','0','0','say6','0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`, `BroadcastTextID`) values('52333','7','0','Nagala will have her famous grilled zhevra chops waiting for us, no doubt. She says I need to cut back and eat more vegetables, but I don\'t think I\'ve gained that much weight....do you?','12','0','100','0','0','0','say7','0');
